// src/App.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    Name: "",
    Type: "",
    Price: "",
    Icindekiler: "",
    Aktif: "",
    BaslamaTarih: "",
    BitisTarih: "",
    Decription: "",
  });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get("/get-products"); // API endpoint
      setProducts(response.data);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  const handleProductSubmit = async () => {
    try {
      await axios.post("/add-product", newProduct); // API endpoint
      fetchProducts(); // Ürünleri güncelle
      setNewProduct({
        Name: "",
        Type: "",
        Price: "",
        Icindekiler: "",
        Aktif: "",
        BaslamaTarih: "",
        BitisTarih: "",
        Decription: "",
      });
    } catch (error) {
      console.error("Error adding product:", error);
    }
  };

  return (
    <div className="App">
      <h1>Ürün Yönetimi</h1>
      <div className="product-form">
        <input
          type="text"
          placeholder="Ürün Adı"
          value={newProduct.Name}
          onChange={(e) =>
            setNewProduct({ ...newProduct, Name: e.target.value })
          }
        />
        {/* Diğer ürün özellikleri için de benzer inputlar */}
        <button onClick={handleProductSubmit}>Ürün Ekle</button>
      </div>
      <div className="product-list">
        {products.map((products) => (
          <div key={products.Id} className="product">
            <h3>{products.Name}</h3>
            {/* Diğer ürün bilgileri */}
            <button>Ürünü Düzenle</button>
            <button>Ürünü Sil</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
