import React, { useState, useEffect } from "react";
import { useAuth } from "../guard/AuthProvider";
import { useEditForm } from "./EditFormContext";
import axios from "axios";
import "./products.css";

export default function Products() {
  const { logout } = useAuth();
  const { editingProduct, setEditingProduct } = useEditForm();
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    Name: "",
    Type: "",
    Price: 0,
    Icindekiler: "",
    Description: "",
  });
  //const [editingProduct, setEditingProduct] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get("http://localhost:5000/products");
      setProducts(response.data);
    } catch (error) {
      console.error("Ürünler alınırken hata oluştu:", error);
    }
  };

  const handleDeleteProduct = async (productId) => {
    try {
      await axios.delete(`http://localhost:5000/product/${productId}`);
      fetchProducts();
    } catch (error) {
      console.error("Ürün silinirken hata oluştu:", error);
    }
  };

  const handleEditProduct = (product) => {
    setEditingProduct(product);

    const editForm = document.querySelector(".edit-form");
    if (editForm) {
      const editFormPosition = editForm.getBoundingClientRect();
      window.scrollTo({
        top: window.scrollY + editFormPosition.top - 20,
        behavior: "smooth",
      });
    }
  };

  const handleUpdateProduct = async () => {
    const { Name, Type, Price, Icindekiler, Description } = newProduct;

    if (
      !Name ||
      typeof Name !== "string" ||
      !Type ||
      typeof Type !== "string" ||
      isNaN(Price) ||
      typeof Price !== "number" ||
      !Icindekiler ||
      typeof Icindekiler !== "string" ||
      !Description ||
      typeof Description !== "string"
    ) {
      console.error("Lütfen tüm alanları doldurun ve uygun veri girin.");
      return;
    }

    try {
      await axios.put(
        `http://localhost:5000/product/${editingProduct.Id}`,
        editingProduct
      );
      setEditingProduct(null);
      fetchProducts();
    } catch (error) {
      console.error("Ürün güncellenirken hata oluştu:", error);
    }
  };

  const cancelEdit = () => {
    setEditingProduct(null);
  };

  const handleAddProduct = async () => {
    const { Name, Type, Price, Icindekiler, Description } = newProduct;

    if (
      !Name ||
      typeof Name !== "string" ||
      !Type ||
      typeof Type !== "string" ||
      isNaN(Price) ||
      typeof Price !== "number" ||
      !Icindekiler ||
      typeof Icindekiler !== "string" ||
      !Description ||
      typeof Description !== "string"
    ) {
      console.error("Lütfen tüm alanları doldurun ve uygun veri girin.");
      return;
    }

    try {
      await axios.post("http://localhost:5000/product", newProduct);
      setNewProduct({
        Name: "",
        Type: "",
        Price: 0,
        Icindekiler: "",
        Description: "",
      });
      fetchProducts();
    } catch (error) {
      console.error("Ürün eklenirken hata oluştu:", error);
    }
  };

  return (
    <div className="urunler-container">
      <button className="logout-button" onClick={logout}>
        Çıkış Yap
      </button>

      <div className="urunler-content">
        <h1>Ürünler</h1>
        <div className="card-list">
          {products.map((product) => (
            <div key={product.Id} className="card">
              <h3>{product.Name}</h3>
              <p>
                <strong>Tür:</strong> {product.Type}
              </p>
              <p>
                <strong>Fiyat:</strong> {product.Price} TL
              </p>
              <p>
                <strong>İçindekiler:</strong> {product.Icindekiler}
              </p>
              <p>
                <strong>Açıklama:</strong> {product.Description}
              </p>
              <div className="card-buttons">
                <button
                  className="action-button"
                  onClick={() => handleEditProduct(product)}
                >
                  Güncelle
                </button>
                <button
                  className="action-button"
                  onClick={() => handleDeleteProduct(product.Id)}
                >
                  Sil
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="urun-ekleme">
        <h2>Ürün Ekle</h2>
        <input
          type="text"
          placeholder="Ürün Adı"
          value={newProduct.Name}
          onChange={(e) =>
            setNewProduct({ ...newProduct, Name: e.target.value })
          }
        />
        <input
          type="text"
          placeholder="Türü"
          value={newProduct.Type}
          onChange={(e) =>
            setNewProduct({ ...newProduct, Type: e.target.value })
          }
        />
        <input
          type="number"
          placeholder="Fiyat"
          value={newProduct.Price}
          onChange={(e) =>
            setNewProduct({ ...newProduct, Price: parseFloat(e.target.value) })
          }
        />
        <input
          type="text"
          placeholder="İçindekiler"
          value={newProduct.Icindekiler}
          onChange={(e) =>
            setNewProduct({ ...newProduct, Icindekiler: e.target.value })
          }
        />
        <input
          type="text"
          placeholder="Açıklama"
          value={newProduct.Description}
          onChange={(e) =>
            setNewProduct({ ...newProduct, Description: e.target.value })
          }
        />
        <button className="action-button" id="edit3" onClick={handleAddProduct}>
          Ekle
        </button>
      </div>

      {editingProduct && (
        <div className="edit-form">
          <h2>Ürün Düzenle</h2>
          <input
            type="text"
            placeholder="Ürün Adı"
            value={editingProduct.Name}
            onChange={(e) =>
              setEditingProduct({ ...editingProduct, Name: e.target.value })
            }
          />
          <input
            type="text"
            placeholder="Türü"
            value={editingProduct.Type}
            onChange={(e) =>
              setEditingProduct({ ...editingProduct, Type: e.target.value })
            }
          />
          <input
            type="number"
            placeholder="Fiyat"
            value={editingProduct.Price}
            onChange={(e) =>
              setEditingProduct({
                ...editingProduct,
                Price: parseFloat(e.target.value),
              })
            }
          />
          <input
            type="text"
            placeholder="İçindekiler"
            value={editingProduct.Icindekiler}
            onChange={(e) =>
              setEditingProduct({
                ...editingProduct,
                Icindekiler: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Açıklama"
            value={editingProduct.Description}
            onChange={(e) =>
              setEditingProduct({
                ...editingProduct,
                Description: e.target.value,
              })
            }
          />
          <button
            className="action-button"
            id="edit"
            onClick={handleUpdateProduct}
          >
            Güncelle
          </button>
          <button className="action-button" id="edit2" onClick={cancelEdit}>
            İptal
          </button>
        </div>
      )}
    </div>
  );
}
